% This function load from the data a similarity graphfunction [similarity_graph] = load_datas()datas = load('data/u.data');n = size(datas(:,1))user_id = datas(:,1);film_id = datas(:,2);ratings = datas(:,3);time = datas(:,4);graph_param.graph_type = 'eps';graph_param.graph_thresh = 0.2; % the number of neighbours for the graphgraph_param.sigma2 = '0.2';similarity_graph = build_similarity_graph(user_id, film_id, ratings, graph_param);